                    @if ($bookings->onFirstPage())
                        <span class="pagination-item pagination-item-prev disabled">
                            <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" viewBox="0 0 35 35"
                                fill="none">
                                <path
