                                    fill="#C1C4CC" />
                            </svg>
                        </span>
                    @else
                        <a href="{{ $bookings->previousPageUrl() }}" class="pagination-item pagination-item-prev">
                            <svg xmlns="http://www.w3.org/2000/svg" width="35" height="35" viewBox="0 0 35 35"
                                fill="none">
